mapper.select:
SELECT DISTINCT e.cn_name AS name, SUBSTRING(e.account, 2) AS employeeNumber, COALESCE(c.cadre_competence_category, e.competence_category, '未知') AS competenceCategory, e.competence_subcategory AS competenceSubcategory, e.departname2, e.departname3, e.departname4, e.departname5, e.departname6, cert.cer_title AS certTitle, cert.start_time AS certStartTime, CASE WHEN exam.emp_num IS NOT NULL THEN 1 ELSE 0 END AS isPassedSubject2, 1 AS isCadre, COALESCE(c.position_ai_maturity, '未知') AS aiMaturity, dept.dept_name AS miniDeptName, c.cadre_type AS cadreType FROM t_cadre c INNER JOIN t_employee e ON (SUBSTRING(e.account, 2) = c.account)
LEFT JOIN ( SELECT DISTINCT COALESCE(employee_number, w3_account) AS employee_number, w3_account, cer_title, start_time FROM dwr_t_cert_record_t WHERE (status = 1 OR approved_status = 1) AND ( -- 华为研究类能力认证（专业级，xxx） cer_title = '华为研究类能力认证（专业级，AI算法技术）' OR cer_title = '华为研究类能力认证（专业级，AI决策推理）' OR cer_title = '华为研究类能力认证（专业级，AI图像语言语义）' ) ) cert ON (e.account = cert.employee_number OR e.account = cert.w3_account)
INNER JOIN ( SELECT DISTINCT COALESCE(employee_number, w3_account) AS employee_number, w3_account, cer_title, start_time FROM dwr_t_cert_record_t WHERE (status = 1 OR approved_status = 1) AND ( -- 华为研究类能力认证（专业级，xxx） cer_title = '华为研究类能力认证（专业级，AI算法技术）' OR cer_title = '华为研究类能力认证（专业级，AI决策推理）' OR cer_title = '华为研究类能力认证（专业级，AI图像语言语义）' ) ) cert ON (e.account = cert.employee_number OR e.account = cert.w3_account)
LEFT JOIN ( SELECT DISTINCT emp_num FROM t_exam_record WHERE exam_name IS NOT NULL AND exam_name != '' AND is_pass = 1 AND emp_num IS NOT NULL AND emp_num != '' ) exam ON (e.sn = exam.emp_num) LEFT JOIN department_info_hrms dept ON (c.mini_departname_id = dept.dept_code AND DATE(dept.update_time) = '2025-11-18') WHERE e.expired_date IS NULL
AND c.mini_departname_id IN
#{deptCode}
AND c.position_ai_maturity IN ('L2', 'L3')
AND COALESCE(c.position_ai_maturity, '未知') = #{aiMaturity}
AND COALESCE(c.cadre_competence_category, '未知') = #{jobCategory}
AND c.account IS NOT NULL AND c.account != '' AND e.account IS NOT NULL AND e.account != '' AND e.account REGEXP '^[a-z]0[0-9]{7}$' ORDER BY e.account

SELECT DISTINCT e.cn_name AS name, SUBSTRING(e.account, 2) AS employeeNumber, COALESCE(c.cadre_competence_category, e.competence_category, '未知') AS competenceCategory, e.competence_subcategory AS competenceSubcategory, e.departname2, e.departname3, e.departname4, e.departname5, e.departname6, q.competence_family_cn AS competenceFamilyCn, q.competence_category_cn AS competenceCategoryCn, q.competence_subcategory_cn AS competenceSubcategoryCn, q.direction_cn_name AS directionCnName, q.competence_rating_cn AS competenceRatingCn, q.competence_grade_cn AS competenceGradeCn, q.competence_from AS competenceFrom, q.competence_to AS competenceTo, COALESCE(c.position_ai_maturity, '未知') AS aiMaturity, 1 AS isCadre, dept.dept_name AS miniDeptName, c.cadre_type AS cadreType FROM t_cadre c INNER JOIN t_employee e ON (SUBSTRING(e.account, 2) = c.account)
LEFT JOIN t_qualifications q ON ( c.account = q.employee_number AND q.employee_number IS NOT NULL AND q.employee_number != '' AND q.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q.competence_from IS NOT NULL AND q.competence_to IS NOT NULL AND q.competence_from = ( -- 子查询：找到每个员工最高级别的AI任职记录的competence_from SELECT q2.competence_from FROM t_qualifications q2 WHERE q2.employee_number = c.account AND q2.employee_number IS NOT NULL AND q2.employee_number != '' AND q2.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q2.competence_from IS NOT NULL AND q2.competence_to IS NOT NULL ORDER BY CASE -- 8级最高，依次到1级，初级最低 WHEN q2.competence_rating_cn = '8级' THEN 1 WHEN q2.competence_rating_cn = '7级' THEN 2 WHEN q2.competence_rating_cn = '6级' THEN 3 WHEN q2.competence_rating_cn = '5级' THEN 4 WHEN q2.competence_rating_cn = '4级' THEN 5 WHEN q2.competence_rating_cn = '3级' THEN 6 WHEN q2.competence_rating_cn = '2级' THEN 7 WHEN q2.competence_rating_cn = '1级' THEN 8 WHEN q2.competence_rating_cn = '初级' THEN 9 ELSE 10 END, q2.competence_from DESC LIMIT 1 ) )
INNER JOIN t_qualifications q ON ( c.account = q.employee_number AND q.employee_number IS NOT NULL AND q.employee_number != '' AND q.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q.competence_from IS NOT NULL AND q.competence_to IS NOT NULL AND q.competence_from = ( -- 子查询：找到每个员工最高级别的AI任职记录的competence_from SELECT q2.competence_from FROM t_qualifications q2 WHERE q2.employee_number = c.account AND q2.employee_number IS NOT NULL AND q2.employee_number != '' AND q2.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q2.competence_from IS NOT NULL AND q2.competence_to IS NOT NULL ORDER BY CASE -- 8级最高，依次到1级，初级最低 WHEN q2.competence_rating_cn = '8级' THEN 1 WHEN q2.competence_rating_cn = '7级' THEN 2 WHEN q2.competence_rating_cn = '6级' THEN 3 WHEN q2.competence_rating_cn = '5级' THEN 4 WHEN q2.competence_rating_cn = '4级' THEN 5 WHEN q2.competence_rating_cn = '3级' THEN 6 WHEN q2.competence_rating_cn = '2级' THEN 7 WHEN q2.competence_rating_cn = '1级' THEN 8 WHEN q2.competence_rating_cn = '初级' THEN 9 ELSE 10 END, q2.competence_from DESC LIMIT 1 ) )
LEFT JOIN department_info_hrms dept ON (c.mini_departname_id = dept.dept_code AND DATE(dept.update_time) = '2025-11-18') WHERE e.expired_date IS NULL
AND c.mini_departname_id IN
#{deptCode}
AND c.position_ai_maturity IN ('L2', 'L3')
AND COALESCE(c.position_ai_maturity, '未知') = #{aiMaturity}
AND COALESCE(c.cadre_competence_category, '未知') = #{jobCategory}
AND c.account IS NOT NULL AND c.account != '' AND e.account IS NOT NULL AND e.account != '' AND e.account REGEXP '^[a-z]0[0-9]{7}$' ORDER BY e.account
