mapper.select:
SELECT DISTINCT e.last_name AS name, e.employee_number AS employeeNumber, COALESCE(c.cadre_competence_category, '未知') AS competenceCategory, CASE WHEN e.job_category LIKE '%-%-%' THEN SUBSTRING_INDEX(e.job_category, '-', -1) ELSE NULL END AS competenceSubcategory, c.departname1 AS departname2, c.departname2 AS departname3, c.departname3 AS departname4, c.departname4 AS departname5, c.departname5 AS departname6, cert.cer_title AS certTitle, cert.start_time AS certStartTime, CASE WHEN exam.emp_num IS NOT NULL THEN 1 ELSE 0 END AS isPassedSubject2, 1 AS isCadre, COALESCE(c.is_cert_standard, 0) AS isCertStandard, COALESCE(c.position_ai_maturity, '未知') AS aiMaturity, SUBSTRING_INDEX(dept.dept_name, '/', 1) AS miniDeptName, c.cadre_type AS cadreType FROM t_cadre c INNER JOIN t_employee_sync e ON (e.employee_number = c.account)
LEFT JOIN ( SELECT DISTINCT employee_number, cer_title, start_time FROM dwr_t_cert_record_t WHERE (status = 1 OR approved_status = 1) AND ( -- 华为研究类能力认证（专业级，xxx） cer_title = '华为研究类能力认证（专业级，AI算法技术）' OR cer_title = '华为研究类能力认证（专业级，AI决策推理）' OR cer_title = '华为研究类能力认证（专业级，AI图像语言语义）' ) AND employee_number IS NOT NULL AND employee_number != '' ) cert ON (e.employee_number = cert.employee_number)
INNER JOIN ( SELECT DISTINCT employee_number, cer_title, start_time FROM dwr_t_cert_record_t WHERE (status = 1 OR approved_status = 1) AND ( -- 华为研究类能力认证（专业级，xxx） cer_title = '华为研究类能力认证（专业级，AI算法技术）' OR cer_title = '华为研究类能力认证（专业级，AI决策推理）' OR cer_title = '华为研究类能力认证（专业级，AI图像语言语义）' ) AND employee_number IS NOT NULL AND employee_number != '' ) cert ON (e.employee_number = cert.employee_number)
LEFT JOIN ( SELECT DISTINCT emp_num FROM t_exam_record WHERE exam_name IS NOT NULL AND exam_name != '' AND is_pass = 1 AND emp_num IS NOT NULL AND emp_num != '' ) exam ON (e.employee_number = exam.emp_num) LEFT JOIN department_info_hrms dept ON (c.mini_departname_id = dept.dept_code AND DATE(dept.update_time) = '2025-11-18') WHERE e.period_id = 20251126
AND c.mini_departname_id IN
#{deptCode}
AND c.position_ai_maturity IN ('L2', 'L3')
AND COALESCE(c.position_ai_maturity, '未知') = #{aiMaturity}
AND COALESCE(c.cadre_competence_category, '未知') = #{jobCategory}
AND c.account IS NOT NULL AND c.account != '' AND e.employee_number IS NOT NULL AND e.employee_number != '' ORDER BY e.employee_number

SELECT ANY_VALUE(e.last_name) AS name, e.employee_number AS employeeNumber, ANY_VALUE(COALESCE(c.cadre_competence_category, '未知')) AS competenceCategory, ANY_VALUE(c.departname1) AS departname2, ANY_VALUE(c.departname2) AS departname3, ANY_VALUE(c.departname3) AS departname4, ANY_VALUE(c.departname4) AS departname5, ANY_VALUE(c.departname5) AS departname6, ANY_VALUE(q.competence_family_cn) AS competenceFamilyCn, ANY_VALUE(q.competence_category_cn) AS competenceCategoryCn, ANY_VALUE(q.competence_subcategory_cn) AS competenceSubcategoryCn, ANY_VALUE(q.direction_cn_name) AS directionCnName, ANY_VALUE(q.competence_rating_cn) AS competenceRatingCn, ANY_VALUE(q.competence_grade_cn) AS competenceGradeCn, ANY_VALUE(q.competence_from) AS competenceFrom, ANY_VALUE(q.competence_to) AS competenceTo, ANY_VALUE(COALESCE(c.position_ai_maturity, '未知')) AS aiMaturity, 1 AS isCadre, ANY_VALUE(SUBSTRING_INDEX(dept.dept_name, '/', 1)) AS miniDeptName, ANY_VALUE(c.cadre_type) AS cadreType, ANY_VALUE(COALESCE(c.is_qualifications_standard, 0)) AS isQualificationsStandard FROM t_cadre c INNER JOIN t_employee_sync e ON (e.employee_number = c.account)
LEFT JOIN t_qualifications q ON ( c.account = q.employee_number AND q.employee_number IS NOT NULL AND q.employee_number != '' AND q.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q.competence_from IS NOT NULL AND q.competence_to IS NOT NULL AND q.competence_from = ( -- 子查询：找到每个员工最高级别的AI任职记录的competence_from SELECT q2.competence_from FROM t_qualifications q2 WHERE q2.employee_number = c.account AND q2.employee_number IS NOT NULL AND q2.employee_number != '' AND q2.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q2.competence_from IS NOT NULL AND q2.competence_to IS NOT NULL ORDER BY CASE -- 8级最高，依次到1级，初级最低 WHEN q2.competence_rating_cn = '8级' THEN 1 WHEN q2.competence_rating_cn = '7级' THEN 2 WHEN q2.competence_rating_cn = '6级' THEN 3 WHEN q2.competence_rating_cn = '5级' THEN 4 WHEN q2.competence_rating_cn = '4级' THEN 5 WHEN q2.competence_rating_cn = '3级' THEN 6 WHEN q2.competence_rating_cn = '2级' THEN 7 WHEN q2.competence_rating_cn = '1级' THEN 8 WHEN q2.competence_rating_cn = '初级' THEN 9 ELSE 10 END, q2.competence_from DESC LIMIT 1 ) )
INNER JOIN t_qualifications q ON ( c.account = q.employee_number AND q.employee_number IS NOT NULL AND q.employee_number != '' AND q.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q.competence_from IS NOT NULL AND q.competence_to IS NOT NULL AND q.competence_from = ( -- 子查询：找到每个员工最高级别的AI任职记录的competence_from SELECT q2.competence_from FROM t_qualifications q2 WHERE q2.employee_number = c.account AND q2.employee_number IS NOT NULL AND q2.employee_number != '' AND q2.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q2.competence_from IS NOT NULL AND q2.competence_to IS NOT NULL ORDER BY CASE -- 8级最高，依次到1级，初级最低 WHEN q2.competence_rating_cn = '8级' THEN 1 WHEN q2.competence_rating_cn = '7级' THEN 2 WHEN q2.competence_rating_cn = '6级' THEN 3 WHEN q2.competence_rating_cn = '5级' THEN 4 WHEN q2.competence_rating_cn = '4级' THEN 5 WHEN q2.competence_rating_cn = '3级' THEN 6 WHEN q2.competence_rating_cn = '2级' THEN 7 WHEN q2.competence_rating_cn = '1级' THEN 8 WHEN q2.competence_rating_cn = '初级' THEN 9 ELSE 10 END, q2.competence_from DESC LIMIT 1 ) )
LEFT JOIN department_info_hrms dept ON (c.mini_departname_id = dept.dept_code AND DATE(dept.update_time) = '2025-11-18') WHERE e.period_id = 20251126
AND c.mini_departname_id IN
#{deptCode}
AND c.position_ai_maturity IN ('L2', 'L3')
AND COALESCE(c.position_ai_maturity, '未知') = #{aiMaturity}
AND COALESCE(c.cadre_competence_category, '未知') = #{jobCategory}
AND c.account IS NOT NULL AND c.account != '' AND e.employee_number IS NOT NULL AND e.employee_number != '' ORDER BY e.employee_number

mapper.update:
UPDATE t_cadre SET l2_department_code = CASE
WHEN account = #{cadre.account} AND mini_departname_id = #{cadre.miniDepartnameId} THEN #{cadre.l2DepartmentCode}
END, l3_department_code = CASE
WHEN account = #{cadre.account} AND mini_departname_id = #{cadre.miniDepartnameId} THEN #{cadre.l3DepartmentCode}
END, l4_department_code = CASE
WHEN account = #{cadre.account} AND mini_departname_id = #{cadre.miniDepartnameId} THEN #{cadre.l4DepartmentCode}
END, l5_department_code = CASE
WHEN account = #{cadre.account} AND mini_departname_id = #{cadre.miniDepartnameId} THEN #{cadre.l5DepartmentCode}
END WHERE (account, mini_departname_id) IN
(#{cadre.account}, #{cadre.miniDepartnameId})

UPDATE t_cadre
SET l2_department_code = #{l2DepartmentCode},
    l3_department_code = #{l3DepartmentCode},
    l4_department_code = #{l4DepartmentCode},
    l5_department_code = #{l5DepartmentCode}
WHERE account = #{account}
  AND mini_departname_id = #{miniDepartnameId}
