mapper.select:
WITH RECURSIVE dept_tree AS ( -- 基础查询：找到起始部门 SELECT dept_code, dept_name, dept_level, parent_dept_code FROM department_info_hrms WHERE dept_code = #{deptCode}
UNION ALL
-- 递归查询：找到所有子部门 SELECT d.dept_code, d.dept_name, d.dept_level, d.parent_dept_code FROM department_info_hrms d INNER JOIN dept_tree dt ON d.parent_dept_code = dt.dept_code ) SELECT DISTINCT d6.dept_code AS deptCode, d6.dept_name AS deptName, d6.dept_level AS deptLevel, d6.parent_dept_code AS parentDeptCode FROM department_info_hrms d6 INNER JOIN dept_tree dt ON d6.dept_code = dt.dept_code WHERE d6.dept_level = '6' ORDER BY d6.dept_code
