mapper.select:
SELECT DISTINCT
    d6.dept_code AS deptCode,
    d6.dept_name AS deptName,
    d6.dept_level AS deptLevel,
    d6.parent_dept_code AS parentDeptCode
FROM department_info_hrms d6
LEFT JOIN department_info_hrms d5 ON d6.parent_dept_code = d5.dept_code
LEFT JOIN department_info_hrms d4 ON d5.parent_dept_code = d4.dept_code
LEFT JOIN department_info_hrms d3 ON d4.parent_dept_code = d3.dept_code
LEFT JOIN department_info_hrms d2 ON d3.parent_dept_code = d2.dept_code
LEFT JOIN department_info_hrms d1 ON d2.parent_dept_code = d1.dept_code
WHERE d6.dept_level = '6'
AND (
    d1.dept_code = #{deptCode}
    OR d2.dept_code = #{deptCode}
    OR d3.dept_code = #{deptCode}
    OR d4.dept_code = #{deptCode}
    OR d5.dept_code = #{deptCode}
    OR d6.parent_dept_code = #{deptCode}
)
ORDER BY d6.dept_code
