SELECT DISTINCT account AS employeeNumber, COALESCE(competence_category, '未知') AS competenceCategory FROM t_employee WHERE expired_date IS NULL
AND department2_id IN
#{deptId}
AND department3_id IN
#{deptId}
AND department4_id IN
#{deptId}
AND department5_id IN
#{deptId}
AND department6_id IN
#{deptId}
AND department7_id IN
#{deptId}
AND 1 = 0
AND account IS NOT NULL AND account != '' AND account REGEXP '^[a-z]0[0-9]{7}$'

SELECT DISTINCT e.cn_name AS name, e.account AS employeeNumber, e.competence_category AS competenceCategory, e.competence_subcategory AS competenceSubcategory, e.departname2, e.departname3, e.departname4, e.departname5, e.departname6, e.departname7, cert.cer_title AS certTitle, cert.start_time AS certStartTime, CASE WHEN exam.emp_num IS NOT NULL THEN 1 ELSE 0 END AS isPassedSubject2 FROM t_employee e INNER JOIN ( SELECT DISTINCT COALESCE(employee_number, w3_account) AS employee_number, w3_account, cer_title, start_time FROM dwr_t_cert_record_t WHERE (status = 1 OR approved_status = 1) AND ( -- 华为研究类能力认证（专业级，xxx） cer_title = '华为研究类能力认证（专业级，AI算法技术）' OR cer_title = '华为研究类能力认证（专业级，AI决策推理）' OR cer_title = '华为研究类能力认证（专业级，AI图像语言语义）' ) ) cert ON (e.account = cert.employee_number OR e.account = cert.w3_account) LEFT JOIN ( SELECT DISTINCT emp_num FROM t_exam_record WHERE exam_name IS NOT NULL AND exam_name != '' AND is_pass = 1 AND emp_num IS NOT NULL AND emp_num != '' ) exam ON (e.sn = exam.emp_num) WHERE e.expired_date IS NULL
AND e.department2_id = #{deptId}
AND e.department3_id = #{deptId}
AND e.department4_id = #{deptId}
AND e.department5_id = #{deptId}
AND e.department6_id = #{deptId}
AND e.department7_id = #{deptId}
AND 1 = 0
AND e.account IS NOT NULL AND e.account != '' AND e.account REGEXP '^[a-z]0[0-9]{7}$' ORDER BY e.account
