SELECT DISTINCT 
    employee_number AS employeeNumber
FROM t_employee_sync
WHERE period_id = 20251126


        AND firstdeptcode IN

            #{deptId}



        AND seconddeptcode IN

            #{deptId}



        AND thirddeptcode IN

            #{deptId}



        AND fourthdeptcode IN

            #{deptId}



        AND fifthdeptcode IN

            #{deptId}



        AND sixthdeptcode IN

            #{deptId}




        AND 1 = 0


AND employee_number IS NOT NULL
AND employee_number != ''

SELECT DISTINCT e.last_name AS name, e.employee_number AS employeeNumber, e.firstdept AS departname2, e.seconddept AS departname3, e.thirddept AS departname4, e.fourthdept AS departname5, e.fifthdept AS departname6, cert.cer_title AS certTitle, cert.start_time AS certStartTime, CASE WHEN exam.emp_num IS NOT NULL THEN 1 ELSE 0 END AS isPassedSubject2, CASE WHEN c.account IS NOT NULL THEN 1 ELSE 0 END AS isCadre FROM t_employee_sync e INNER JOIN ( SELECT DISTINCT COALESCE(employee_number, w3_account) AS employee_number, w3_account, cer_title, start_time FROM dwr_t_cert_record_t WHERE (status = 1 OR approved_status = 1) AND ( -- 华为研究类能力认证（专业级，xxx） cer_title = '华为研究类能力认证（专业级，AI算法技术）' OR cer_title = '华为研究类能力认证（专业级，AI决策推理）' OR cer_title = '华为研究类能力认证（专业级，AI图像语言语义）' ) ) cert ON (e.employee_number = cert.employee_number OR e.employee_number = cert.w3_account) LEFT JOIN ( SELECT DISTINCT emp_num FROM t_exam_record WHERE exam_name IS NOT NULL AND exam_name != '' AND is_pass = 1 AND emp_num IS NOT NULL AND emp_num != '' ) exam ON (e.employee_number = exam.emp_num) LEFT JOIN t_cadre c ON (e.employee_number = c.account) WHERE e.period_id = 20251126
AND e.firstdeptcode = #{deptId}
AND e.seconddeptcode = #{deptId}
AND e.thirddeptcode = #{deptId}
AND e.fourthdeptcode = #{deptId}
AND e.fifthdeptcode = #{deptId}
AND e.sixthdeptcode = #{deptId}
AND 1 = 0
AND e.employee_number IS NOT NULL AND e.employee_number != '' ORDER BY e.employee_number
