SELECT DISTINCT 
    employee_number AS employeeNumber
FROM t_employee_sync
WHERE period_id = 20251126


        AND firstdeptcode IN

            #{deptId}



        AND seconddeptcode IN

            #{deptId}



        AND thirddeptcode IN

            #{deptId}



        AND fourthdeptcode IN

            #{deptId}



        AND fifthdeptcode IN

            #{deptId}



        AND sixthdeptcode IN

            #{deptId}




        AND 1 = 0


AND employee_number IS NOT NULL
AND employee_number != ''

SELECT DISTINCT e.last_name AS name, e.employee_number AS employeeNumber, CASE WHEN e.job_category LIKE '%-%-%' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(e.job_category, '-', 2), '-', -1) WHEN e.job_category LIKE '%-%' THEN SUBSTRING_INDEX(e.job_category, '-', -1) ELSE e.job_category END AS competenceCategory, CASE WHEN e.job_category LIKE '%-%-%' THEN SUBSTRING_INDEX(e.job_category, '-', -1) ELSE NULL END AS competenceSubcategory, SUBSTRING_INDEX(e.firstdept, '/', 1) AS departname2, SUBSTRING_INDEX(e.seconddept, '/', 1) AS departname3, SUBSTRING_INDEX(e.thirddept, '/', 1) AS departname4, SUBSTRING_INDEX(e.fourthdept, '/', 1) AS departname5, SUBSTRING_INDEX(e.fifthdept, '/', 1) AS departname6, SUBSTRING_INDEX(e.sixthdept, '/', 1) AS departname7, cert.cer_title AS certTitle, cert.start_time AS certStartTime, CASE WHEN exam.emp_num IS NOT NULL THEN 1 ELSE 0 END AS isPassedSubject2, CASE WHEN c.account IS NOT NULL THEN 1 ELSE 0 END AS isCadre, c.cadre_type AS cadreType, COALESCE(c.is_cert_standard, 0) AS isCertStandard, CASE WHEN exp.account IS NOT NULL THEN 1 ELSE 0 END AS isExpert, COALESCE(exp.position_ai_maturity, c.position_ai_maturity, NULL) AS aiMaturity, SUBSTRING_INDEX(e.lowest_dept, '/', 1) AS miniDeptName, NULL AS isQualificationsStandard FROM t_employee_sync e
LEFT JOIN ( SELECT c1.employee_number, c1.w3_account, c1.cer_title, c1.start_time FROM dwr_t_cert_record_t c1 INNER JOIN ( SELECT COALESCE(employee_number, w3_account) AS emp_key, MAX(start_time) AS max_start_time FROM dwr_t_cert_record_t WHERE (status = 1 OR approved_status = 1) AND ( cer_title = '华为研究类能力认证（专业级，AI算法技术）' OR cer_title = '华为研究类能力认证（专业级，AI决策推理）' OR cer_title = '华为研究类能力认证（专业级，AI图像语言语义）' ) GROUP BY COALESCE(employee_number, w3_account) ) c2 ON ( COALESCE(c1.employee_number, c1.w3_account) = c2.emp_key AND c1.start_time = c2.max_start_time ) WHERE (c1.status = 1 OR c1.approved_status = 1) AND ( c1.cer_title = '华为研究类能力认证（专业级，AI算法技术）' OR c1.cer_title = '华为研究类能力认证（专业级，AI决策推理）' OR c1.cer_title = '华为研究类能力认证（专业级，AI图像语言语义）' ) ) cert ON ( (e.employee_number = cert.employee_number OR e.employee_number = cert.w3_account) )
INNER JOIN ( SELECT c1.employee_number, c1.w3_account, c1.cer_title, c1.start_time FROM dwr_t_cert_record_t c1 INNER JOIN ( SELECT COALESCE(employee_number, w3_account) AS emp_key, MAX(start_time) AS max_start_time FROM dwr_t_cert_record_t WHERE (status = 1 OR approved_status = 1) AND ( cer_title = '华为研究类能力认证（专业级，AI算法技术）' OR cer_title = '华为研究类能力认证（专业级，AI决策推理）' OR cer_title = '华为研究类能力认证（专业级，AI图像语言语义）' ) GROUP BY COALESCE(employee_number, w3_account) ) c2 ON ( COALESCE(c1.employee_number, c1.w3_account) = c2.emp_key AND c1.start_time = c2.max_start_time ) WHERE (c1.status = 1 OR c1.approved_status = 1) AND ( c1.cer_title = '华为研究类能力认证（专业级，AI算法技术）' OR c1.cer_title = '华为研究类能力认证（专业级，AI决策推理）' OR c1.cer_title = '华为研究类能力认证（专业级，AI图像语言语义）' ) ) cert ON ( (e.employee_number = cert.employee_number OR e.employee_number = cert.w3_account) )
LEFT JOIN ( SELECT DISTINCT emp_num FROM t_exam_record WHERE exam_name IS NOT NULL AND exam_name != '' AND is_pass = 1 AND emp_num IS NOT NULL AND emp_num != '' ) exam ON (e.employee_number = exam.emp_num) LEFT JOIN t_cadre c ON (e.employee_number = c.account) LEFT JOIN t_expert exp ON (e.employee_number = exp.account) WHERE e.period_id = 20251126 AND e.employee_number IS NOT NULL AND e.employee_number != ''
AND e.firstdeptcode = #{deptId}
AND e.seconddeptcode = #{deptId}
AND e.thirddeptcode = #{deptId}
AND e.fourthdeptcode = #{deptId}
AND e.fifthdeptcode = #{deptId}
AND e.sixthdeptcode = #{deptId}
AND 1 = 0
AND ( CASE WHEN e.job_category LIKE '%-%-%' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(e.job_category, '-', 2), '-', -1) WHEN e.job_category LIKE '%-%' THEN SUBSTRING_INDEX(e.job_category, '-', -1) ELSE e.job_category END ) != '软件类'
AND ( CASE WHEN e.job_category LIKE '%-%-%' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(e.job_category, '-', 2), '-', -1) WHEN e.job_category LIKE '%-%' THEN SUBSTRING_INDEX(e.job_category, '-', -1) ELSE e.job_category END ) NOT IN ('研究类', '软件类', '系统类', '测试类')
AND ( CASE WHEN e.job_category LIKE '%-%-%' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(e.job_category, '-', 2), '-', -1) WHEN e.job_category LIKE '%-%' THEN SUBSTRING_INDEX(e.job_category, '-', -1) ELSE e.job_category END ) = #{jobCategory}
ORDER BY e.employee_number, COALESCE(cert.start_time, '1900-01-01')

SELECT DISTINCT 
    employee_number AS employeeNumber,
    job_category AS competenceCategory
FROM t_employee_sync
WHERE period_id = 20251126


        AND firstdeptcode IN

            #{deptCode}



        AND seconddeptcode IN

            #{deptCode}



        AND thirddeptcode IN

            #{deptCode}



        AND fourthdeptcode IN

            #{deptCode}



        AND fifthdeptcode IN

            #{deptCode}



        AND sixthdeptcode IN

            #{deptCode}



        AND 1 = 0


AND employee_number IS NOT NULL
AND employee_number != ''

SELECT DISTINCT e.last_name AS name, e.employee_number AS employeeNumber, CASE WHEN e.job_category LIKE '%-%-%' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(e.job_category, '-', 2), '-', -1) WHEN e.job_category LIKE '%-%' THEN SUBSTRING_INDEX(e.job_category, '-', -1) ELSE e.job_category END AS competenceCategory, CASE WHEN e.job_category LIKE '%-%-%' THEN SUBSTRING_INDEX(e.job_category, '-', -1) ELSE NULL END AS competenceSubcategory, SUBSTRING_INDEX(e.firstdept, '/', 1) AS departname2, SUBSTRING_INDEX(e.seconddept, '/', 1) AS departname3, SUBSTRING_INDEX(e.thirddept, '/', 1) AS departname4, SUBSTRING_INDEX(e.fourthdept, '/', 1) AS departname5, SUBSTRING_INDEX(e.fifthdept, '/', 1) AS departname6, SUBSTRING_INDEX(e.sixthdept, '/', 1) AS departname7, q.competence_family_cn AS competenceFamilyCn, q.competence_category_cn AS competenceCategoryCn, q.competence_subcategory_cn AS competenceSubcategoryCn, q.direction_cn_name AS directionCnName, q.competence_rating_cn AS competenceRatingCn, q.competence_grade_cn AS competenceGradeCn, q.competence_from AS competenceFrom, q.competence_to AS competenceTo, CASE WHEN c.account IS NOT NULL THEN 1 ELSE 0 END AS isCadre, c.cadre_type AS cadreType, CASE WHEN exp.account IS NOT NULL THEN 1 ELSE 0 END AS isExpert, COALESCE(exp.position_ai_maturity, c.position_ai_maturity, NULL) AS aiMaturity, SUBSTRING_INDEX(e.lowest_dept, '/', 1) AS miniDeptName, NULL AS isQualificationsStandard, NULL AS isCertStandard FROM t_employee_sync e
LEFT JOIN t_qualifications q ON ( e.employee_number = q.employee_number AND q.employee_number IS NOT NULL AND q.employee_number != '' AND q.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q.competence_from IS NOT NULL AND q.competence_to IS NOT NULL AND CURDATE() BETWEEN q.competence_from AND q.competence_to AND q.competence_from = ( -- 子查询：找到每个员工最高级别的AI任职记录的competence_from SELECT q2.competence_from FROM t_qualifications q2 WHERE q2.employee_number = e.employee_number AND q2.employee_number IS NOT NULL AND q2.employee_number != '' AND q2.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q2.competence_from IS NOT NULL AND q2.competence_to IS NOT NULL AND CURDATE() BETWEEN q2.competence_from AND q2.competence_to ORDER BY CASE -- 8级最高，依次到1级，初级最低 WHEN q2.competence_rating_cn = '8级' THEN 1 WHEN q2.competence_rating_cn = '7级' THEN 2 WHEN q2.competence_rating_cn = '6级' THEN 3 WHEN q2.competence_rating_cn = '5级' THEN 4 WHEN q2.competence_rating_cn = '4级' THEN 5 WHEN q2.competence_rating_cn = '3级' THEN 6 WHEN q2.competence_rating_cn = '2级' THEN 7 WHEN q2.competence_rating_cn = '1级' THEN 8 WHEN q2.competence_rating_cn = '初级' THEN 9 ELSE 10 END, q2.competence_from DESC LIMIT 1 ) )
INNER JOIN t_qualifications q ON ( e.employee_number = q.employee_number AND q.employee_number IS NOT NULL AND q.employee_number != '' AND q.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q.competence_from IS NOT NULL AND q.competence_to IS NOT NULL AND CURDATE() BETWEEN q.competence_from AND q.competence_to AND q.competence_from = ( -- 子查询：找到每个员工最高级别的AI任职记录的competence_from SELECT q2.competence_from FROM t_qualifications q2 WHERE q2.employee_number = e.employee_number AND q2.employee_number IS NOT NULL AND q2.employee_number != '' AND q2.direction_cn_name IN ( '数据科学与AI工程（ICT）', 'AI算法及应用（ICT）', 'AI软件工程与工具（ICT）', 'AI系统测试（ICT）' ) AND q2.competence_from IS NOT NULL AND q2.competence_to IS NOT NULL AND CURDATE() BETWEEN q2.competence_from AND q2.competence_to ORDER BY CASE -- 8级最高，依次到1级，初级最低 WHEN q2.competence_rating_cn = '8级' THEN 1 WHEN q2.competence_rating_cn = '7级' THEN 2 WHEN q2.competence_rating_cn = '6级' THEN 3 WHEN q2.competence_rating_cn = '5级' THEN 4 WHEN q2.competence_rating_cn = '4级' THEN 5 WHEN q2.competence_rating_cn = '3级' THEN 6 WHEN q2.competence_rating_cn = '2级' THEN 7 WHEN q2.competence_rating_cn = '1级' THEN 8 WHEN q2.competence_rating_cn = '初级' THEN 9 ELSE 10 END, q2.competence_from DESC LIMIT 1 ) )
LEFT JOIN t_cadre c ON (e.employee_number = c.account) LEFT JOIN t_expert exp ON (e.employee_number = exp.account) WHERE e.period_id = 20251126 AND e.employee_number IS NOT NULL AND e.employee_number != ''
AND e.firstdeptcode = #{deptId}
AND e.seconddeptcode = #{deptId}
AND e.thirddeptcode = #{deptId}
AND e.fourthdeptcode = #{deptId}
AND e.fifthdeptcode = #{deptId}
AND e.sixthdeptcode = #{deptId}
AND 1 = 0
AND ( CASE WHEN e.job_category LIKE '%-%-%' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(e.job_category, '-', 2), '-', -1) WHEN e.job_category LIKE '%-%' THEN SUBSTRING_INDEX(e.job_category, '-', -1) ELSE e.job_category END ) != '软件类'
AND ( CASE WHEN e.job_category LIKE '%-%-%' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(e.job_category, '-', 2), '-', -1) WHEN e.job_category LIKE '%-%' THEN SUBSTRING_INDEX(e.job_category, '-', -1) ELSE e.job_category END ) NOT IN ('研究类', '软件类', '系统类', '测试类')
AND ( CASE WHEN e.job_category LIKE '%-%-%' THEN SUBSTRING_INDEX(SUBSTRING_INDEX(e.job_category, '-', 2), '-', -1) WHEN e.job_category LIKE '%-%' THEN SUBSTRING_INDEX(e.job_category, '-', -1) ELSE e.job_category END ) = #{jobCategory}
ORDER BY e.employee_number, COALESCE(q.competence_from, '1900-01-01')
