mapper.select:
SELECT DISTINCT course_team_code
FROM (
    SELECT DISTINCT course_team_code
    FROM t_micro_study_info_sync
    WHERE emp_num = #{empNum}
      AND course_team_code IN 

          #{courseNumber}

      AND is_pass = '1'

    UNION

    SELECT DISTINCT course_team_code
    FROM t_mooc_study_info_sync
    WHERE emp_num = #{empNum}
      AND course_team_code IN 

          #{courseNumber}

      AND is_pass = '1'
) AS completed_courses
